import { createSlice } from "@reduxjs/toolkit";

const Products = createSlice({
    name: 'Products',
    initialState: {
        value: [],
    },
    reducers: {
        update(state, arr) {
            state.value = arr;
        },
        delete(state, arr) {
            state.value.pop(arr)
        }
    },
})

export const {update} = Products.actions

export default Products.reducer;
