import React from 'react';
import { useSelector } from 'react-redux';

import styles from './styles/ProdList.module.scss';

import SliderCart from '../../entities/SliderCart/index.tsx';

import l from '../../img/inst1.png'

import { getAllProducts } from './api/api.ts'; 
import { addProducts } from '../../redux/slices/Products';
import { useDispatch } from 'react-redux';

type CartType = {
    img: string,
    title: string,
    cost: string,
}

const ProductsList = () => {
    let prodList = useSelector((state: any) => state.products.value);
    const [allProducts, setAllProducts] = React.useState([]);

    // let bool = prodList.length !== 0;

    getAllProducts().then((res: any) => {
        setAllProducts(res)
    })

    return (
        <div className={styles.block}>
            { prodList.length !== 0 
                ? prodList.map((product: any) => {
                return (
                    <SliderCart img={l} ttl={product.title} cost={product.cost}/>
                    )
                }) 

                : allProducts.map((elem: any) => {
                    return (
                        <SliderCart img={l} ttl={elem.title} cost={elem.cost}/>
                    )
                }) 
            }
        </div>
    );
};

export default ProductsList;
