import { createSlice } from "@reduxjs/toolkit";

const Products = createSlice({
    name: 'Products',
    initialState: {
        value: [],
    },
    reducers: {
        update(arr) {
            initialState.value = arr;
        }
    },
})

export const {update} = Products.actions

export default Products.reducer;
