import React from 'react';
import TopHeader from '../../widgets/TopHeader/TopHeader';
import Footer from '../../widgets/Footer/index.tsx';

import styles from './styles/FAQ.module.scss';

import background from '../../img/faq-background.png';

const FAQPage = () => {

    function toggleOpened(event: React.MouseEvent<HTMLElement>) {
        const currentTarget = event.currentTarget as HTMLElement;
        const nextSibling = currentTarget.nextElementSibling as HTMLElement | null;
        const firstChild = currentTarget.firstElementChild as HTMLElement | null;
      
        if (nextSibling) {
          nextSibling.classList.toggle(styles.paragraphDisabled);
        }
      
        if (firstChild) {
          if (firstChild.innerHTML === '+') {
            firstChild.innerHTML = '-';
          } else {
            firstChild.innerHTML = '+';
          }
        }
    }

    const questionsData = [
        {
          id: 1,
          question: 'БУДЕТ ЛИ ЗАКАЗАННЫЙ БУКЕТ В ТОЧНОСТИ СООТВЕТСТВОВАТЬ ЕГО ИЗОБРАЖЕНИЮ НА САЙТЕ?',
          answer: 'Букет собирается по индивидуальной матрице букета. Однако возможны случаи, когда нет необходимых цветов либо имеющиеся цветы отличаются от представленных на фото, и с согласования заказчика они могут быть заменены на аналогичные. Либо если флорист считает, что данные изменения не повлекут сильного изменения в образе букета, то самостоятельно может заменить некоторые цветы. И перед отправкой направляется фото заказчику, который утверждает получившийся букет. Каждый цветок отличается от другого, как и каждый букет будет индивидуальным, но в этом и есть его прелесть… в индивидуальности.',
          isOpen: false
        },
        {
          id: 2,
          question: 'КАК ДОЛГО СТОЯТ БУКЕТЫ ИЗ СУХОЦВЕТОВ?',
          answer: 'Текст ответа на второй вопрос...',
          isOpen: false
        },
        {
          id: 3,
          question: 'ДЕЛАЕТЕ ЛИ ВЫ ФОТО ГОТОВОГО БУКЕТА ПЕРЕД ОТПРАВКОЙ?',
          answer: 'Текст ответа на третий вопрос...',
          isOpen: false
        },
        {
          id: 4,
          question: 'А СКОЛЬКО ДОЛЖНЫ ПРОСТОЯТЬ ЦВЕТЫ В БУКЕТЕ?',
          answer: 'Текст ответа на четвертый вопрос...',
          isOpen: false
        },
        {
          id: 5,
          question: 'ЕСТЬ ЛИ ДОСТАВКА ЗА ПРЕДЕЛЫ МКАД?',
          answer: 'Текст ответа на пятый вопрос...',
          isOpen: false
        },
        {
          id: 6,
          question: 'КАК ОПРЕДЕЛИТЬ СВЕЖИЕ ЛИ ЦВЕТЫ?',
          answer: 'Текст ответа на шестой вопрос...',
          isOpen: false
        },
        {
          id: 7,
          question: 'ЧЕМ ОТЛИЧАЕТСЯ БЕЛОРУССКАЯ РОЗА ОТ ИМПОРТНОЙ?',
          answer: 'Текст ответа на седьмой вопрос...',
          isOpen: false
        }
      ];
      

    return (
        <div className={styles.block}>
            <TopHeader />
            <img className={styles.background} src={background} alt="" />
            <div className={styles.wrapper}>
                <span className={styles.navLink}><a href='/'>Главная </a> / <a href='/questions'>faq</a></span>
                <h2 className={styles.ttl}>faq</h2>

                <div className={styles.questionBlock}>
                    {questionsData.map((item: {id: number, question: string, answer: string, isOpen: boolean}) => { 
                        return (
                        <div className={styles.question} key={item.id}>
                            <h2 className={styles.questionTtl} onClick={toggleOpened}> {item.question} <span className={styles.openCharacter}>-</span></h2>
                            <p className={styles.questionParagraph}>{item.answer}</p>
                        </div>
                        )
                    })}
                </div>
            </div>
            <Footer />
        </div>
    );
};

export default FAQPage;
