import React from 'react';
import { useForm } from 'react-hook-form';
import { Controller } from 'react-hook-form';
import styles from './styles/SortByProps.module.scss';

type Inputs = {
    example: string
    exampleRequired: string
}

const SortByProps = () => {
    const filters = [
        {ttl: 'ПО СВЕТУ', variants: ['нежные', 'нежные']},
        {ttl: 'ПО ЦВЕТУ', variants: ['нежные', 'нежные', 'нежные', 'нежные', 'нежные', 'нежные', 'нежные']},
        {ttl: 'ПО ФОРМАТУ', variants: ['нежные', 'нежные', 'нежные', 'нежные', 'нежные', 'нежные']},
        {ttl: 'СТОИМОСТЬ', variants: ['нежные', 'нежные']},
        {ttl: 'ПО ЦВЕТКУ', variants: ['нежные', 'нежные', 'нежные', 'нежные', 'нежные', 'нежные', 'нежные', 'нежные', 'нежные', 'нежные', 'нежные', 'нежные', 'нежные', 'нежные']},
    ]

    const {
        register,
        handleSubmit,
        control,
        formState: { errors },
    } = useForm()

    const sendProps = () => {

    };

    const changeHandler = (event: React.MouseEvent<HTMLButtonElement>) => {
        event?.currentTarget.previousElementSibling?.classList.toggle(styles.active);
    };

    return (
        <form className={styles.block} onSubmit={handleSubmit(sendProps)}>
            {filters.map(topic => {
                return (
                    <div className="">
                        <h2 className={styles.title}>{topic.ttl}</h2>
                        <div className={styles.inputs}>
                            {topic.variants.map((vari) => {
                                return (
                                    <label className={styles.label}>
                                        <div className={styles.fakeCheckbox}></div>
                                        <input className={styles.checkbox} type={'checkbox'} {...register("Light", { onChange: changeHandler })}/>
                                        <span className={styles.inputText}>{vari}</span>
                                    </label>
                                )
                            })}
                        </div>
                    </div>
                );
            })}
            <h2 className={styles.title}>По свету</h2>
            <div className={styles.inputs}>
                <label className={styles.label}>
                    <div className={styles.fakeCheckbox}></div>
                    <input className={styles.checkbox} type={'checkbox'} {...register("Light", { onChange: changeHandler })}/>
                    <span className={styles.inputText}>нежные</span>
                </label>
            </div>
            <h2 className={styles.title}>Пo цвету</h2>
                    
            
            <h2 className={styles.title}>По формату</h2>
                   
            
            <h2 className={styles.title}>стоимость</h2>
            
            <h2 className={styles.title}>По цветку</h2>
                 
        </form>
    );
};

export default SortByProps;
