import { createSlice, current } from "@reduxjs/toolkit";

const Products = createSlice({
    name: 'Products',
    initialState: {
        value: [],
    },
    reducers: {
        addProducts(state, arr) {
            console.log(current(state.value), (arr.payload));
            const res = state.value.concat(arr.payload);
            state.value = res
        },
        deleteProducts(state, arr) {
            state.value.concat(arr)
        },
        update(state, arr) {
            state.value = arr;
        }
    },
})

export const {addProducts, deleteProducts, update} = Products.actions

export default Products.reducer;
