import { createSlice } from '@reduxjs/toolkit';

const BusketSlice = createSlice({
    name: 'Busket',
    initialState: {
        value: 0,
    },
    reducers: {
        increment() {
            value++
        },
        decrement() {
            value--
        },
    },
})

export const {increment, decrement} = BusketSlice.actions

export default BusketSlice.reducer;
