import React from 'react';
import styles from './styles/SortBy.module.scss';
import arrow from '../../img/arrow-sort-by.svg';

import { useDispatch, useSelector } from 'react-redux';
import { updateValue } from '../../redux/slices/SortBy'; 
import { update } from '../../redux/slices/Products';
// import { pushParam, popParam } from '../../redux/slices/Params';
import getCurrentproducts from './api/api.ts';

const SortBy = () => {
    const dispatch = useDispatch();

    const types = useSelector((state: any) => state.prodTypes);
    const sortBy = useSelector((state: any) => state.sortby);
    const props = useSelector((state: any) => state.Params);

    let [store, setStore] = React.useState();

    async function handle (event: React.MouseEvent<HTMLButtonElement>) {
        await dispatch(updateValue(event.currentTarget.innerText))

        // take targetElem props
        // const name = event?.currentTarget.name;
        // const inputValue = event?.currentTarget.nextElementSibling?.innerHTML;
        
        const obj = { types, sortBy, props };

        // UPD State for Params
        // if (event?.currentTarget.previousElementSibling?.classList.contains(styles.active)) {
        //     dispatch(popParam({[name]: inputValue}))
        // } else {
        //     dispatch(pushParam({[name]: inputValue}))
        // }

        // Make request
        
        makeRequest(obj)
    };
   
    function makeRequest(obj: any) {
        try {
            getCurrentproducts(obj)
            .then((res: any) => {
                dispatch(update(res));
            })
        } catch (err) {
            console.error(err)
        }
    }

    return (
        <div className={styles.block}>
            <div className={styles.head}>
                <span className={styles.headTtl} datatype='rating'>По популярности</span>
                <img className={styles.arrow} src={arrow} alt="arrow" />
            </div>
            <div className={styles.body}>
                <span className={styles.headTtl} datatype='Increasing' onClick={handle}>По возростанию</span><br />
                <span className={styles.headTtl} datatype='Decreasing' onClick={handle}>По убыванию</span>
            </div>
        </div>
    );
};

export default SortBy;
